import React, { useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput } from 'react-native';
const Login = ({ navigation }) => {
  const [userName, setUserName] = useState('');
  const [Password, setPassword] = useState('')
  let value = { username: 'test', password: 'test123', notes: [] };
  const storeData = async (value) => {
    try {
      const jsonValue = JSON.stringify(value)
      await AsyncStorage.setItem('test', jsonValue)
    } catch (e) {
      // saving error
    }
  }

  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('test');
      if (jsonValue != null) {
        console.log(JSON.parse(jsonValue));
        console.log(JSON.parse(jsonValue).password);
        return (JSON.parse(jsonValue));
      }
      else {
        return null;
      }
    } catch (e) {
      // error reading value
    }
  }
  const checkCredentials = () => {
    console.log(userName, Password);
    const storedData = JSON.parse(getData());
    console.log(storedData);
    if (storedData == null) {
      alert('Create Account');
      return false;
    }
    else {
      if (Password == storedData.Password) {
        return true;
      }
    }
  }
  return (
    <View style={styles.container}>
      <Text> in Login</Text>
      <View>
        <TextInput
          style={styles.input}
          onChangeText={setUserName}
          placeholder="Enter Username"
        />
        <TextInput
          style={styles.input}
          onChangeText={setPassword}
          placeholder="Enter password"
        />
        <Button title="Login" onPress={() => {
          if(checkCredentials()) {
            return navigation.navigate('Notes');
           }
        }}></Button>
      </View>
      <Button title="store data" onPress={() => { storeData(value) }}></Button>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
export default Login;
