import React, { useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';

const Notes = ({ navigation }) => {
  //const [userData, setUserData] = useState({});
  let userData;
  //let currentUser;
  let notes;
  const [currentUser,setCurrentUser]=useState('');
  // const [notes,setNotes]=useState({});
  const storeData = async (value) => {
    try {
      const jsonValue = JSON.stringify(value)
      await AsyncStorage.setItem('notesAndUserData', jsonValue)
    } catch (e) {
      // saving error
    }
  }
  useEffect(async () => {
    await getData();
    //await getNotes();
  }, []);
  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('notesAndUserData');
      const data = jsonValue && JSON.parse(jsonValue);
      console.log("At getting data", data);
      userData = data;
      // setUserData(data);
       setCurrentUser(data.currentUser.username);
      //currentUser = data.currentUser.username;
      console.log(currentUser);
      if (!data.notes.name) {
        userData.notes[currentUser] = [];
        storeData(userData);
      }
      console.log(userData);
      notes = userData.notes[currentUser];
      //getNotes();
    } catch (e) {
      // error reading value
    }
  }
  const showNotes = () => {
    return (
      <View >
        {userData.notes[1].map((note, index) => {
          return (
            <TouchableOpacity
              style={{ borderWidth: 1, borderColor: 'grey', padding: 5, marginVertical: 5, flex: 1, flexDirection: 'row', elevation: 2 }}
              onPress={() => { navigation.navigate('Notes', Notes); }}
              key={index}
            >
              <Text style={{ marginHorizontal: 4 }}>{note.title}</Text>
            </TouchableOpacity>
          )
        })}
      </View>
    );
  }
  const logout = () => {
    userData.currentUser.username = "";
    //console.log("userData",userData);
    storeData(userData);
    navigation.navigate('Login');
  }
  const getNotes=()=>{
    console.log("at Get Note",currentUser);
     return (<Text>{currentUser}</Text>);
  }
  return (
    <View>
      <Text>In Notes</Text>
      <TouchableOpacity onPress={() => {
        logout()
      }}>
        <Text>Logout</Text>
      </TouchableOpacity>
      <View >
        {currentUser && getNotes()}
      </View>
    </View>

  );
}
export default Notes;
