import React, { useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
import notesAnduserData from './notesAndUserData';
const CreatAccount = ({navigation}) => {
    const [userName, setUserName] = useState('');
    const [Password, setPassword] = useState('');
    const [userData, setUserData] = useState({});
    const storeData = async (value) => {
        try {
          const jsonValue = JSON.stringify(value)
          await AsyncStorage.setItem('notesAndUserData', jsonValue)
        } catch (e) {
          // saving error
        }
      }
      useEffect(() => {
        getData();
      }, []);
      const getData = async () => {
        try {
          const jsonValue = await AsyncStorage.getItem('notesAndUserData');
          const data = jsonValue && JSON.parse(jsonValue);
          console.log(data);
          setUserData(data);
          return data;
        } catch (e) {
          // error reading value
        }
      }
      const addUser=()=>{
        let isExist = false;
        if(userName.length<3){
          alert("Enter Valid userName length greater than 3");
        }
        if(Password.length<1){
          alert("Enter a valid password");
        }
        for (let i = 0; i < userData.users.length; i++) {
          if(userData.users[i].username == userName){
            isExist=true;
            alert("User Name exits try another");
          }
        }
        if(!isExist){
          userData.users.push({username:userName,password:Password});
        }
        console.log(userData);
        storeData(userData);
        navigation.navigate("Login");
      }
    const showData = ()=>{
      console.log(userData.users);
      //userData.users.push({username:"demo",password:"demo"});
      for (let i = 0; i < userData.users.length; i++) {
        console.log(userData.users[i].username);
      }
      console.log(userData);
    }
    return (
        <View style={styles.container}>
        <Text styles={styles.signuptext}>signup</Text>
        <View style={styles.signup}> 
          <TextInput
            style={styles.inputbox}
            onChangeText={setUserName}
            placeholder="Enter Username"
          />
          <TextInput
            style={styles.inputbox}
            onChangeText={setPassword}
            placeholder="Enter password"
          />
          <Button title="Sign Up" onPress={() => { addUser() }}></Button>
        </View>
      
      </View>
      
    );
}
const styles = StyleSheet.create({
  container: {
    margin: 0,
    padding: 0,
    flex: 1,
    backgroundColor: 'bisque',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial',
  },
  container1: {
    margin: 0,
    padding: 0,
    backgroundColor: '#fff',
    fontFamily: 'Arial',
  },
  signuptext :{
    textAlign: 'center',
    color : 'red',
    padding : 20,
    fontSize: 30,
  },
  signup:{
    justifyContent:'center',
    height: 300,
    width: 300,
    overflow: 'hidden',  
    marginTop:10,
    padding: 5,
    backgroundColor: 'mintcream',
    borderRadius:15,
  },
  inputbox:{
    flexDirection:'row',
    height:40,
    padding:10,
    borderRadius:15,
    marginHorizontal:15,
    border:3,
    borderColor:'white'
  },
  signupbutton:{
    marginTop:30,
  },
  signup:{
    marginTop:10,
    justifyContent:'flex-start',
    alignContent:'flex-end',
    color:'white'
  }
  });
export default CreatAccount;
