import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Button, Image, ScrollView, TouchableOpacity } from 'react-native';
React.useLayoutEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <Button onPress={() => {
          console.log("Log out pressed");
          userData.currentUser.username='';
          storeData(userData);
          navigation.navigate('Login');
        }} title="Logout" />
      ),
    });
  }, [navigation, userData]);
const ShowNote = ({ route, navigation }) => {
    const data = route.params;
    React.useLayoutEffect(() => {
        navigation.setOptions({
          headerRight: () => (
            <Button onPress={() => {
              console.log("Log out pressed");
              userData.currentUser.username='';
              storeData(userData);
              navigation.navigate('Login');
            }} title="Logout" />
          ),
        });
      }, [navigation, userData]);
    return (

        <View style={styles.container}>
            <ScrollView>
                <Text style={{ fontSize: 15, fontWeight: 'bold', padding: 6, margin: 3, marginVertical: 20, borderColor: 'red', borderWidth: 1, backgroundColor: '#FFD580' }}>{data.title}</Text>
                <View style={{ borderWidth: 1,height:450, borderColor: 'grey', backgroundColor: '#FFFDD9' }}>
                    <Text style={{ fontWeight: 'normal'}}>{data.description}</Text>
                </View>
                <TouchableOpacity
                    style={styles.savebutton}
                    onPress={() => {
                        navigation.goBack()
                    }
                    }
                >
                    <Text>Back</Text>
                </TouchableOpacity>
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        margin: 0,
        padding: 0,
        flex: 1,
        flexDirection: 'column'
    },
    savebutton: {
        marginTop: 'auto',
        alignItems: "center",
        backgroundColor: "yellowgreen",
        padding: 10
    }
});
export default ShowNote;
