import { DELETE_NOTE } from "./noteTypes";
const initialState = {
    id: '',
    notes: {}

}

const noteReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_NOTE: return {
            ...state,
            id: action.payload,
            user: action.user
        }
        case STORE_NOTE: return {
            ...state,
            notes: action.payload
        }

        default: return state
    }
}
export default noteReducer;
