import React, { useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
const Login = ({ navigation }) => {
  const [userName, setUserName] = useState('');
  const [Password, setPassword] = useState('');
  const [userData, setUserData] = useState({});
  let value = { username: 'test', password: 'test123', notes: [] };
  const storeData = async (value) => {
    try {
      const jsonValue = JSON.stringify(value)
      await AsyncStorage.setItem('test', jsonValue)
    } catch (e) {
      // saving error
    }
  }
  // const navigateOnCondition = () => {
  //   let condition = checkCredentials();
  //   console.log(condition);
  //   if (condition) {
  //     navigation.navigate('Notes');
  //   }
  // }
  useEffect(() => {

    getData();
  }, []);
  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('notesAndUserData');
      const data = jsonValue && JSON.parse(jsonValue);
      console.log(data);
      setUserData(data);
      console.log(userData);
      return data;
    } catch (e) {
      // error reading value
    }
  }

  const checkCredentials = () => {
    console.log(userName, Password);
    console.log(userData);
    const users=userData?.users;
    if(!users){
      users = [];
    }
    if (userData != null) {
      let isExist=false;
      for (let i = 0; i < userData.users.length; i++) {
        if(userData.users[i].username == userName){
          isExist=true;
          if (Password == userData.users[i].password) {
            console.log("in true");
            navigation.navigate('Notes');
          }
          else{
            alert('Wrong Password')
          }
        }
      }
      if(!isExist){
        console.log("Need to creat account");
        alert('Create Account');
      }
      
    }
  }
  return (
    <View style={styles.container}>
      <Text> in Login</Text>
      <View>
        <TextInput
          style={styles.input}
          onChangeText={setUserName}
          placeholder="Enter Username"
        />
        <TextInput
          style={styles.input}
          onChangeText={setPassword}
          placeholder="Enter password"
        />
        <Button title="Login" onPress={() => {
          checkCredentials();
        }}></Button>
      </View>
      <TouchableOpacity onPress={()=>{ navigation.navigate('CreateAccount')}}>
      <Text>Sign Up</Text>
    </TouchableOpacity>
    </View>
    
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
export default Login;
