import React, { useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput, TouchableOpacity, ScrollView } from 'react-native';

const Notes = ({ navigation }) => {
  const [userData, setUserData] = useState({});
  //let userData;
  //let currentUser;
  //let notes;
  const [currentUser, setCurrentUser] = useState('');
  const [notes, setNotes] = useState([]);
  const storeData = async (value) => {
    try {
      const jsonValue = JSON.stringify(value)
      await AsyncStorage.setItem('notesAndUserData', jsonValue)
    } catch (e) {
      // saving error
    }
  }
  useEffect(async () => {
    await getData();
    //await getNotes();
  }, [notes]);
  const getData = async () => {
    try {
      const jsonValue = await AsyncStorage.getItem('notesAndUserData');
      const data = jsonValue && JSON.parse(jsonValue);
      console.log("At getting data", data);
      //userData = data;
      setUserData(data);
      setCurrentUser(data.currentUser.username);
      //currentUser = data.currentUser.username;
      console.log(currentUser);
      if (!data.notes.name) {
        userData.notes[currentUser] = [];
        storeData(userData);
      }
      console.log(userData);
      setNotes(userData.notes[currentUser])
      //notes = userData.notes[currentUser];
      //getNotes();
    } catch (e) {
      // error reading value
    }
  }
  const deleteNote = (id) => {
    console.log("id to be deleted", id);
  }
  const showNotes = () => {
    return (
      <View >
        {userData.notes[currentUser].map((note, index) => {
          return (
            <TouchableOpacity
              style={{ borderWidth: 1, borderColor: 'grey', padding: 5, marginVertical: 5, flex: 1, flexDirection: 'column', elevation: 2 }}
              onPress={() => { navigation.navigate('ShowNote', note); }}
              key={index}
            >
              <Text style={{ marginHorizontal: 4, fontWeight: 'bold',flexDirection:'column'}} numberOfLines={1} >{note.title}</Text>
              <Text style={{flexDirection:'column'}}>{
                note.description.length < 35
                  ? `${note.description}`
                  : `${note.description.substring(0, 32)}...`}</Text>
              <TouchableOpacity style={{width:10,justifyContent:'flex-end',borderWidth:2,borderColor:'black',}}onPress={() => { deleteNote(note.id) }} >
                <Text>Delete</Text>
              </TouchableOpacity>
            </TouchableOpacity>
          )
        })}
      </View>
    );
  }
  const logout = () => {
    userData.currentUser.username = "";
    //console.log("userData",userData);
    storeData(userData);
    navigation.navigate('Login');
  }
  const addNotes = () => {
    navigation.navigate('AddNotes');
  }
  return (

    <View style={styles.container1}>
      <ScrollView>
        <Text>In Notes</Text>
        <TouchableOpacity onPress={() => {
          addNotes()
        }}>
          <Text>Add Notes</Text>
        </TouchableOpacity>
        <View >
          {currentUser && showNotes()}
        </View>
      </ScrollView>
    </View>


  );
}
const styles = StyleSheet.create({
  container1: {
    margin: 0,
    padding: 0,
    flex: 1,
    flexDirection: 'column',
    backgroundColor: 'khaki'
  }


});
export default Notes;
