import { DELETE_NOTE, STORE_NOTES } from "./noteTypes";
import initialState from "../initialState";
import noteActions from "..";
// const initialState = {
//     id: '',
//     notes: {}

// }

const noteReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_NOTE: return {
            ...state,
            id: action.payload,
            user: action.user,
            notes: state.notes.filter((item) => item.id !== state.id)
        }
        case STORE_NOTES: return {
            ...state,
            notes: action.payload
        }

        default: return state
    }
}
export default noteReducer;
