import React, { useEffect, useState } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Button, StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
import notesAnduserData from './notesAndUserData';
const CreatAccount = ({navigation}) => {
    const [userName, setUserName] = useState('');
    const [Password, setPassword] = useState('');
    const [userData, setUserData] = useState({});
    const storeData = async (value) => {
        try {
          const jsonValue = JSON.stringify(value)
          await AsyncStorage.setItem('notesAndUserData', jsonValue)
        } catch (e) {
          // saving error
        }
      }
      useEffect(() => {
        getData();
      }, []);
      const getData = async () => {
        try {
          const jsonValue = await AsyncStorage.getItem('notesAndUserData');
          const data = jsonValue && JSON.parse(jsonValue);
          console.log
          console.log(data);
          setUserData(data);
          console.log(userData);
          return data;
        } catch (e) {
          // error reading value
        }
      }
    return (
        <View style={styles.container}>
        <Text> in signup</Text>
        <View>
          <TextInput
            style={styles.input}
            onChangeText={setUserName}
            placeholder="Enter Username"
          />
          <TextInput
            style={styles.input}
            onChangeText={setPassword}
            placeholder="Enter password"
          />
        </View>
        <Button title="store data" onPress={() => { storeData(value) }}></Button>
        <TouchableOpacity onPress={()=>{ navigation.navigate('CreateAccount')}}>
        <Text>Sign Up</Text>
      </TouchableOpacity>
      </View>
      
    );
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  });
export default CreatAccount;
